import styled from "styled-components";
import get from "lodash/get";

export const Layout = styled.div`
  border: 1px solid ${({ theme }) => get(theme, "colors.primary", "")};
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 5px;
  height: 80px;
  cursor: pointer;
  &:hover {
    border: 1px solid ${({ theme }) => get(theme, "colors.secondary", "")};
    background-color: ${({ theme }) => get(theme, "colors.secondary", "")};
  }
`;

export const MovieTitle = styled.span`
  font-size: 16px;
  font-weight: bold;
  align-self: start;
  width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
`;

export const MovieYear = styled.span`
  font-size: 16px;
  text-align: right;
`;

export const SelectedStar = styled.div`
  position: relative;
  height: 0px;
  width: 0px;
  border-right: 100px solid transparent;
  border-bottom: 70px solid gold;
  border-left: 100px solid transparent;

  transform: rotate(35deg);

  :before {
    position: absolute;
    top: -45px;
    left: -65px;
    content: "";
    border-bottom: 80px solid gold;
    border-left: 30px solid transparent;
    border-right: 30px solid transparent;

    transform: rotate(-35deg);
  }

  :after {
    position: absolute;
    top: 3px;
    left: -105px;
    content: "";
    border-right: 100px solid transparent;
    border-bottom: 70px solid gold;
    border-left: 100px solid transparent;

    transform: rotate(-70deg);
  }
`;
