import styled from "styled-components";
import get from "lodash/get";

export const SearchBox = styled.input`
  width: 100%;
  border: 1px solid ${({ theme }) => get(theme, "colors.primary", "")};
  color: ${({ theme }) => get(theme, "colors.primary", "")};
  border-radius: 4px;
  height: 50px;
  padding-left: 5px;
`;
