import React from "react";
import { withApollo } from "react-apollo";
import gql from "graphql-tag";
import { useDispatch, useSelector } from "react-redux";
import { moviePageNumSelector } from "../../selectors";
import { IState } from "../../types";
import { Container } from "./styled";

interface IMoviePager {
  client: any;
}

const MoviePager: React.FC<IMoviePager> = ({ client }) => {
  const page = useSelector((state: IState) => moviePageNumSelector(state));
  const totalResults = client.readFragment({
    fragment: gql`
      fragment movies on movies {
        totalResults
      }
    `
  });

  return (
    <Container>
      <h1>{page}</h1>
      <h1>{totalResults}</h1>
    </Container>
  );
};

export default withApollo(MoviePager);
